const Test = () => {
    const Chose = (id: string) => {
        const ip: any = document.getElementById('a')
        const text: any = document.getElementById(id)
        ip.value = text.innerText
    }
    const change = () => {
        const ip: any = document.getElementById('a')
        const div: any = document.getElementById('abc')
        if (ip.value == 'đỏ') {
            div.style.display = 'block';
        } else {
            div.style.display = 'none';
        }
    }
    const show = () => {
        const ip: any = document.getElementById('aaa')
        if (ip.style.display == 'none') {
            ip.style.display = 'block';
        } else {
            ip.style.display = 'none';
        }
    }
    return (
        <div>
            <div onClick={() => change()} style={{ marginTop: 300, marginLeft: 700 }}>
                <div className="row"><input id='a' type="text " name='a' value={'tất cả'}></input>
                    <p onClick={() => show()} >  show</p></div>
                <div id='aaa' style={{ display: 'none' }} onClick={() => show()}>
                    <p onClick={() => Chose('b')} id='b'>nguyen duy duc</p>
                    <p onClick={() => Chose('c')} id='c'>đỏ</p>
                    <p onClick={() => Chose('d')} id='d'>xanh</p>
                </div>
            </div>
            <div id='do' style={{ backgroundColor: 'red', display: 'none', height: 100, width: 100 }}>aaaaaaaaaaaa</div>
            <div id='xanh' style={{ backgroundColor: 'green', display: 'none', height: 100, width: 100 }}>aaaaaaaaaaaa</div>
        </div>
    )
}

export default Test
