import React from 'react';

import '../layout/menubar';
import { Topbar } from '../layout/topbar';
import { Menubar } from '../layout/menubar';
import { AiOutlinePlus, AiOutlineSearch } from "react-icons/ai";
import TableDevice from './tabale-device';
import Nextpage from '../layout/nextpage';
import { RiArrowDownSLine } from 'react-icons/ri';

class Device extends React.Component {
    Chose = (id: string) => {
        const ip: any = document.getElementById('ip')
        const text: any = document.getElementById(id)
        ip.value = text.innerText
    }

    show = () => {
        const ip: any = document.getElementById('aaa')
        if (ip.style.display == 'none') {
            ip.style.display = 'block';
        } else {
            ip.style.display = 'none';
        }
    }
    render() {
        return (

            <div className='bg'>
                <div className="Backgroundapp">
                    <TableDevice />
                    <a href='/Device/AddDevice'>
                        <div className='div-add'>
                            <div className='plus'><AiOutlinePlus className='cong' /></div>
                            <div className='add'>
                                <p > Thêm </p>
                                <p >thiết bị</p>
                            </div>
                        </div>
                    </a>

                    <div className='tthd'>
                        <p className='ppp'>Trạng thái hoạt động</p>
                        <input id='ip' className='tb-select' value={'Tất cả'} ></input>
                        <button onClick={() => this.show()}><RiArrowDownSLine /></button>
                        <div id='aaa' className='select-drop-down' onClick={() => this.show()}>
                            <div className='select-drop-down-item ' onClick={() => this.Chose('tc')}><a id='tc'>Tất cả</a></div>
                            <div className='select-drop-down-item ' onClick={() => this.Chose('hd')}><a id='hd'>Hoạt động</a></div>
                            <div className='select-drop-down-item ' onClick={() => this.Chose('nhd')}><a id='nhd'>Ngừng hoạt động</a></div>
                        </div>

                    </div>
                    <div className='ttkn'>
                        <p className='ppp'>Trạng thái kết nối</p>
                        <select className='tb-select'>
                            <option value="all">Tất cả</option>
                            <option value="conect">Kết nối</option>
                            <option value="unconect">Mất kết nối</option>
                        </select>
                    </div>
                    <div className='tk'>
                        <p className='ppp'>Từ khóa</p>
                        <div className='sreach'>
                            <form >
                                <input type="text" name='timkiem' placeholder="Nhập từ khóa ..."></input>
                                <button type='submit'><AiOutlineSearch /></button>
                            </form>
                        </div>
                    </div>
                    <div className='title-report-type'>Danh sách thiết bị</div>

                    <Nextpage />
                    <Topbar nametitle1='Thiết bị ﹥ ' nametitle2='' nametitle3='Danh sách thiết bị' href='' href2='' nametitle21='' />
                    <Menubar buttonid="tb" />

                </div>
            </div>
        );

    }


}

export default Device;
