import React from "react";

class Nextpage extends React.Component {
    onclick(id: string) {
        const btn: any = document.getElementById(id)
        btn.classList.toggle("page-number-onclick")
        document.addEventListener("mousedown", (event) => {
            btn.style.display = 'none';
        })
    }
    render() {
        return (
            <div className="row-nextpage">
                <div className="page-number" ><p>◀</p></div>
                <div className="page-number" id="1" onClick={() => this.onclick('1')}><p>1</p></div>
                <div className="page-number" id="2" onClick={() => this.onclick('2')}><p>2</p></div>
                <div className="page-number" id="3" onClick={() => this.onclick('3')}><p>3</p></div>
                <div className="page-number" id="4" onClick={() => this.onclick('4')}><p>4</p></div>
                <div className="page-number" id="5" onClick={() => this.onclick('5')}><p>5</p></div>
                <div className="page-number" id="..." onClick={() => this.onclick('...')}><p>...</p></div>
                <div className="page-number" id="10" onClick={() => this.onclick('10')}><p>10</p></div>
                <div className="page-number"><p>▶</p></div>
            </div>
        )
    }
}
export default Nextpage;
