const Test = () => {
    const Chose = (id: string) => {
        const ip: any = document.getElementById('a')
        const text: any = document.getElementById(id)
        ip.value = text.innerText
    }
    const change = () => {
        const ip: any = document.getElementById('a')
        if (ip.value == 'duc') {
            const div: any = document.getElementById('abc')
            div.style.display = 'none';
        }
    }
    const show = () => {
        const ip: any = document.getElementById('aaa')
        if (ip.style.display == 'none') {
            ip.style.display = 'block';
        } else {
            ip.style.display = 'none';
        }
    }
    return (
        <div>
            <form onClick={() => change()}>
                <input id='a' type="text " name='a' value={'tất cả'}></input>
                <p onClick={() => show()} >  show</p>
                <div id='aaa' style={{ display: 'none' }} onClick={() => show()}>
                    <p onClick={() => Chose('b')} id='b'>nguyen duy duc</p>
                    <p onClick={() => Chose('c')} id='c'>duc</p>
                </div>
                <button>aaaaaa</button>
                <div id='abc' style={{ color: 'red', display: 'block' }}>aaaaaaaaaaaa</div>
            </form>
        </div>
    )
}

export default Test
