import ChartImg from './chart.svg';


export const Chart = () => {
    var type: string = "";
    const handleChange = () => {
        const btn: any = document.querySelector("ty");
        type = 'duc';
    }

    return (
        <div className='chart'>
            <span>thống kê theo {type}</span>
            <select className='ty' onChange={handleChange}>
                <option value="A">Apple</option>
                <option value="B">Banana</option>
                <option value="C">Cranberry</option>
            </select>
            <img src={ChartImg} alt="chart" />
        </div >
    )
}
