import ChartImg from './chart.svg';
import { Doughnut } from "react-chartjs-2";
import React, { FC, useState } from 'react'

type FruitProps = {
    defaulchart: string;
}
let newDate = new Date()
let date = newDate.getDate();
let month = newDate.getMonth() + 1;
let year = newDate.getFullYear();
export const Chart: FC<FruitProps> = ({ defaulchart }) => {
    const [currentType, setCurrentType] = useState(defaulchart)
    var setday: string
    const changetype = (newType: string): void => {
        setCurrentType(newType)
        daytime(currentType)
    }

    const daytime = (a: string) => {
        if (a === 'tuần') {
            setday = 'Tuần ' + date + "/" + month + "/" + year
        } else
            if (a === 'ngày') {
                setday = 'Ngày ' + date + "/" + month + "/" + year
            } else
                if (a === 'tháng') {
                    setday = 'Tháng ' + month + '/' + year
                }
        return setday;

    }
    return (
        <div className='chart'>
            <span className='title-chart'>Bảng thống kê theo {currentType} </span>
            <p >{daytime(currentType)}</p>
            <div className='select-type-row'>
                <span>Xem thêm</span>
                <form  >
                    <select className='select-type' onChange={(event) => changetype(event.target.value)} value={currentType}>
                        <option value="ngày">Ngày</option>
                        <option value="tuần">Tuần</option>
                        <option value="tháng">Tháng</option>
                    </select>
                </form>
            </div>
            {/* <img src={ChartImg} alt="chart" /> */}
            <Line
                data={{
                    labels: [1500, 1600, 1700, 1750, 1800, 1850, 1900, 1950, 1999, 2050],
                    datasets: [
                        {
                            data: [86, 114, 106, 106, 107, 111, 133, 221, 783, 2478],
                            label: "Africa",
                            borderColor: "#3e95cd",
                            fill: false
                        },
                        {
                            data: [282, 350, 411, 502, 635, 809, 947, 1402, 3700, 5267],
                            label: "Asia",
                            borderColor: "#8e5ea2",
                            fill: false
                        },
                        {
                            data: [168, 170, 178, 190, 203, 276, 408, 547, 675, 734],
                            label: "Europe",
                            borderColor: "#3cba9f",
                            fill: false
                        },
                        {
                            data: [40, 20, 10, 16, 24, 38, 74, 167, 508, 784],
                            label: "Latin America",
                            borderColor: "#e8c3b9",
                            fill: false
                        },
                        {
                            data: [6, 3, 2, 2, 7, 26, 82, 172, 312, 433],
                            label: "North America",
                            borderColor: "#c45850",
                            fill: false
                        }
                    ]
                }}
                options={{
                    title: {
                        display: true,
                        text: "World population per region (in millions)"
                    },
                    legend: {
                        display: true,
                        position: "bottom"
                    }
                }}
            />
        </div>
    )
}




export default Chart;
