import ChartImg from './chart.svg';




import React, { FC, useState } from 'react'

type FruitProps = {
    fruitDetector: string;
}

export const Chart: FC<FruitProps> = ({ fruitDetector }) => {
    const [currentFruit, setCurrentFruit] = useState(fruitDetector)

    const changeFruit = (newFruit: string): void => {
        setCurrentFruit(newFruit)
    }

    return (
        <div className='chart'>
            <span>ng√†y {currentFruit} </span>
            <form>
                <select
                    onChange={(event) => changeFruit(event.target.value)}
                    value={currentFruit}
                >
                    <option value="apples">Red Apples</option>
                    <option value="oranges">Outrageous Oranges</option>
                    <option value="tomatoes">Technically a Fruit Tomatoes</option>
                    <option value="bananas">Bodacious Bananas</option>
                </select>
            </form>
        </div>
    )
}




export default Chart;
