import React from 'react';
import logo from './images/logo.svg';
import htqlxh from './images/htqlxh.svg';
import { Link } from "react-router-dom"


class Login extends React.Component {
  checklogin() {
    (e: React.SyntheticEvent) => {
      e.preventDefault();
      const target = e.target as typeof e.target & {
        name: { value: string };
        password: { value: string };
      };
      const name = target.name.value; // typechecks!
      const password = target.password.value; // typechecks!
      if (name == "duc" && password == "123"
      ) {
        alert('Your favorite flavor is: ' + name)
      }
      else {

      }
    }
  }
  render() {
    return (
      <div className='bg'>
        <div className="Backgroundapp">
          <div>
            <div className='logo'>
              <img src={logo} className="App-logo" alt="logo" />
            </div>
            <div className='myform'>
              <form
                onSubmit={this.checklogin}
              >
                <div className='col1'>
                  <p >Tên đăng nhập*</p>
                  <input type="text" id="name" name='name'></input>
                  <p >Mật khẩu*</p>
                  <input type="password" id="password" name='password'></input>
                  <div><Link to={'/ressetpw'}>Quên mật khẩu?</Link></div>
                </div>
                <div className='col2'>
                  <div className='magin'>

                    <div>
                      <button type='submit' >Đăng nhập</button>
                    </div>

                  </div>
                </div>
              </form>
            </div>
          </div>
          <div className='imagedangnhap'>
            <div className='hinh'>
              <img src={htqlxh} alt="htqlxh" />
            </div>
          </div>
        </div>
      </div>
    );
  }
}
export default Login;
