import ChartImg from './chart.svg';




import React, { FC, useState } from 'react'

type FruitProps = {
    defaulchart: string;
}

export const Chart: FC<FruitProps> = ({ defaulchart }) => {
    const [currentType, setCurrentType] = useState(defaulchart)

    const changetype = (newFruit: string): void => {
        setCurrentType(newFruit)
    }

    return (
        <div className='chart'>
            <span>ngày {currentType} </span>
            <form>
                <select onChange={(event) => changetype(event.target.value)} value={currentType}>
                    <option value="ngày">ngày</option>
                    <option value="tháng">tháng</option>
                    <option value="năm">năm</option>

                </select>
            </form>
        </div>
    )
}




export default Chart;
